/**
 * A MIME parser/serializer module, extended from the @jsdom/whatwg-mimetype library.
 * Visit source code on GitHub: https://github.com/jsdom/whatwg-mimetype
 */
export interface MimeType {
    type: string;
    subtype: string;
    parameters: Map<string, string | string[]>;
}
export interface MimeTypeOptions {
    arrayParameters?: boolean;
}
/**
 * Convert MIME string to MimeType object.
 * @example
 * parseMimeType('image/svg+xml;q=";";q=0.8')
 * // {
 * //  type: 'image',
 * //  subtype: 'svg+xml',
 * //  parameters: Map(1) { 'q' => ';' },
 * // }
 */
export declare function parseMimeType(input: string, options?: MimeTypeOptions): MimeType | null;
/**
 * Convert MIME string to MimeType object.
 * @example
 * parseMimeType({
 *   type: 'image',
 *   subtype: 'svg+xml',
 *   parameters: new Map([
 *     ['a', '1'],
 *     ['b', '2'],
 *   ]),
 * })
 * // image/svg+xml;a=1;b=2
 */
export declare function serializeMimeType(mimeType: MimeType, options?: MimeTypeOptions): string;
export declare function removeLeadingAndTrailingHTTPWhitespace(string: string): string;
export declare function removeTrailingHTTPWhitespace(string: string): string;
export declare function isHTTPWhitespaceChar(char: string): boolean;
export declare function solelyContainsHTTPTokenCodePoints(string: string): boolean;
export declare function solelyContainsExtendedHTTPTokenCodePoints(string: string): boolean;
export declare function solelyContainsHTTPQuotedStringTokenCodePoints(string: string): boolean;
export declare function asciiLowercase(string: string): string;
export declare function collectAnHTTPQuotedString(input: string, position: number): [string, number];
